import React,{useState} from 'react';
import {useSelector} from 'react-redux';

const Dropdown = () => {

    const elementColor = useSelector(state => state.switching.elementColor);
    const textColor = useSelector(state => state.switching.textColor);
    const [filter,setRegion] = useState('Filter by Region');

    const setLocation = (event) => {
        setRegion(event.target.value)
    }

    return(
        <div className='dropDown'>
            <button style={{backgroundColor:elementColor,color:textColor}} className='defaultOption'>
                <p>{filter}</p>
                <i className="fa fa-angle-down" aria-hidden="true"></i>
            </button>
            <div style={{backgroundColor:elementColor,color:textColor}} className='list'>
                <ul>
                    <li>
                       <button onClick={setLocation} style={{color:textColor}}  value='Africa'>Africa</button> 
                    </li>
                    <li value='America'>
                        <button onClick={setLocation} style={{color:textColor}}>America</button>
                    </li>
                    <li value='Asia'>
                       <button onClick={setLocation} style={{color:textColor}}> Asia</button>
                    </li>
                    <li  value='Europe'>
                       <button onClick={setLocation} style={{color:textColor}}>Europe</button> 
                    </li>
                    <li  value='Oceania'>
                       <button onClick={setLocation} style={{color:textColor}}> Oceania</button> 
                    </li>
                </ul>
            </div>
        </div>
    )
}

export default Dropdown;
