export const switching= (state={textColor:'hsl(200, 15%, 8%)',backgroundColor:'hsl(0, 0%, 98%)',elementColor:'hsl(0, 0%, 100%)'},action) => {
switch(action.type){
    case 'DARK':
    return {...state,textColor:'hsl(0, 0%, 100%)',backgroundColor:'hsl(207, 26%, 17%)',elementColor:'hsl(209, 23%, 22%)'}

    default:
    return state;
}
}
