import React from 'react';
import Border from './Border';
import clickFlag from './action/index.js';
// import {useSelector,useDispatch} from 'react-redux';


const Detail = ({name,nativeName,population,region,subregion,capital,topLevelDomain,currencies,languages,borders,flag}) => {
    const dispatch = useDispatch();
    return(
        <div className = 'Detail'>
             <div onClick={() => dispatch(clickFlag(''))} className='back'>
                <i className="fa fa-long-arrow-left" aria-hidden="true"></i>
                <div>Back</div>
             </div> 
             <div className='infoContainer'>
                <img src={flag} alt=''></img>
               {/* right info box */}
                    <div className='info'>
                        <h1>{name}</h1> 
                        {/* middle info */}
                        <div className='infoDetail'>
                            <div className='leftInfo'>
                                <div>
                                    <strong>Native Name:</strong> {` ${nativeName}`}
                                </div>
                                <div>
                                    <strong>Population:</strong> {` ${population}`}
                                </div>
                                <div>
                                    <strong>Region:</strong> {` ${region}`}
                                </div>
                                <div>
                                    <strong>Sub Region:</strong> {` ${subregion}`}
                                </div>
                                <div>
                                    <strong>Capital:</strong> {` ${capital}`}
                                </div>
                            </div>
                            <div className='rightInfo'>
                                 <div className='domain'>
                                    <strong>Top Level Domain:</strong> {` ${topLevelDomain}`}
                                </div>
                                <div className='currencies'>
                                    <strong>Currencies:</strong> {currencies.map((currency,i) => {
                                        return <div className='currency' key={i}>{currency.name}</div>
                                    })}
                                </div>
                                <div className='languages'>
                                    <strong>Languages:</strong> {languages.map((language,i) => {
                                        return <div key={i} className='language'>{language.name}</div>
                                    })}
                                </div>
                            </div>
                        </div>
                        {/* bottom border countries */}
                        <div className='borders'>
                        <strong>Border Countries:</strong>
                            {borders.map((border,i) => {
                                return <Border key={i} countryCode={border}/>
                            })}
                        </div>
                    </div>


             </div>
        </div>
    )
}

export default Detail;
