import React,{useState} from 'react';
import {useSelector,useDispatch} from 'react-redux';
import {setLocation} from './actions/index.js';

const Dropdown = () => {
    const dispatch = useDispatch();
    const elementColor = useSelector(state => state.switching.elementColor);
    const textColor = useSelector(state => state.switching.textColor);
    const [filter,setRegion] = useState('Filter by Region');

    const setLocation = (event) => {
        setRegion(event.target.value)
    }

    return(
        <div className='dropDown'>
            <button style={{backgroundColor:elementColor,color:textColor}} className='defaultOption'>
                <p>{filter}</p>
                <i className="fa fa-angle-down" aria-hidden="true"></i>
            </button>
            <div style={{backgroundColor:elementColor,color:textColor}} className='list'>
                <ul>
                    <li>
                       <button onClick={dispatch()} style={{color:textColor}}  value='Africa'>Africa</button> 
                    </li>
                    <li>
                        <button onClick={setLocation} style={{color:textColor}}  value='America'>America</button>
                    </li>
                    <li>
                       <button onClick={setLocation} style={{color:textColor}}  value='Asia'> Asia</button>
                    </li>
                    <li >
                       <button onClick={setLocation} style={{color:textColor}}  value='Europe'>Europe</button> 
                    </li>
                    <li>
                       <button onClick={setLocation} style={{color:textColor}}   value='Oceania'> Oceania</button> 
                    </li>
                </ul>
            </div>
        </div>
    )
}

export default Dropdown;
